import React, { useState } from 'react';
import { exportAPI } from '../../services/api';
import { handleExport } from '../../utils/exportUtils';

const ExportModal = ({ show, onClose, user, exportType = 'reports' }) => {
  const [dateRange, setDateRange] = useState({
    startDate: '',
    endDate: ''
  });
  const [exporting, setExporting] = useState(false);

  if (!show) return null;

  const getExportFunction = () => {
    const params = new URLSearchParams();
    
    params.append('role', user.role);
    params.append('userId', user.id);
    
    if (user.faculty) {
      params.append('faculty', user.faculty);
    }
    
    if (dateRange.startDate) {
      params.append('startDate', dateRange.startDate);
    }
    
    if (dateRange.endDate) {
      params.append('endDate', dateRange.endDate);
    }

    switch (exportType) {
      case 'reports':
        return () => exportAPI.reports(params.toString());
      case 'courses':
        return exportAPI.courses;
      case 'ratings':
        return () => exportAPI.ratings(user.faculty || '');
      default:
        return () => exportAPI.reports(params.toString());
    }
  };

  const getExportTitle = () => {
    switch (exportType) {
      case 'reports':
        return 'Export Teaching Reports';
      case 'courses':
        return 'Export Courses';
      case 'ratings':
        return 'Export Student Ratings';
      default:
        return 'Export Data';
    }
  };

  const handleExportClick = async () => {
    setExporting(true);
    
    const exportFunction = getExportFunction();
    const result = await handleExport(exportFunction, {}, `luct-${exportType}-export`);
    
    if (result.success) {
      onClose();
    } else {
      alert(result.message);
    }
    
    setExporting(false);
  };

  return (
    <div className="modal show d-block" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">{getExportTitle()}</h5>
            <button 
              type="button" 
              className="btn-close" 
              onClick={onClose}
              disabled={exporting}
            ></button>
          </div>
          
          <div className="modal-body">
            {exportType === 'reports' && (
              <div className="mb-3">
                <label className="form-label">Date Range (Optional)</label>
                <div className="row">
                  <div className="col-md-6">
                    <input
                      type="date"
                      className="form-control"
                      placeholder="Start Date"
                      value={dateRange.startDate}
                      onChange={(e) => setDateRange(prev => ({ ...prev, startDate: e.target.value }))}
                    />
                  </div>
                  <div className="col-md-6">
                    <input
                      type="date"
                      className="form-control"
                      placeholder="End Date"
                      value={dateRange.endDate}
                      onChange={(e) => setDateRange(prev => ({ ...prev, endDate: e.target.value }))}
                    />
                  </div>
                </div>
                <small className="text-muted">
                  Leave empty to export all reports
                </small>
              </div>
            )}
            
            <div className="alert alert-info">
              <strong>Export includes:</strong>
              <ul className="mb-0 mt-1">
                {exportType === 'reports' && (
                  <>
                    <li>Report details and metadata</li>
                    <li>Attendance information</li>
                    <li>Teaching topics and outcomes</li>
                    <li>Lecturer recommendations</li>
                    <li>PRL feedback</li>
                    <li>Student ratings</li>
                  </>
                )}
                {exportType === 'courses' && (
                  <>
                    <li>Course codes and names</li>
                    <li>Assigned lecturers</li>
                    <li>Stream information</li>
                    <li>Report statistics</li>
                    <li>Average ratings</li>
                  </>
                )}
                {exportType === 'ratings' && (
                  <>
                    <li>Student ratings and comments</li>
                    <li>Course and lecturer information</li>
                    <li>Rating statistics</li>
                    <li>Date information</li>
                  </>
                )}
              </ul>
            </div>
          </div>
          
          <div className="modal-footer">
            <button 
              type="button" 
              className="btn btn-secondary" 
              onClick={onClose}
              disabled={exporting}
            >
              Cancel
            </button>
            <button 
              type="button" 
              className="btn btn-success" 
              onClick={handleExportClick}
              disabled={exporting}
            >
              {exporting ? (
                <>
                  <span className="spinner-border spinner-border-sm me-2" role="status">
                    <span className="visually-hidden">Loading...</span>
                  </span>
                  Exporting...
                </>
              ) : (
                '📊 Download Excel File'
              )}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ExportModal;
