import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { reportsAPI, ratingsAPI } from '../../services/api';

const ReportDetail = () => {
  const { reportId } = useParams();
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const [report, setReport] = useState(null);
  const [ratings, setRatings] = useState([]);
  const [averageRating, setAverageRating] = useState(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadReportDetails();
  }, [reportId]);

  const loadReportDetails = async () => {
    try {
      // Mock data for testing
      setTimeout(() => {
        setReport({
          id: parseInt(reportId),
          week_of_reporting: 'Week 6',
          date_of_lecture: '2024-10-01',
          class_name: 'BIT-1A',
          course_name: 'Web Application Development',
          course_code: 'DIWA2110',
          lecturer_name: 'Dr. John Smith',
          actual_students_present: 38,
          topic_taught: 'Introduction to React and Component Architecture. We covered functional components, JSX syntax, props, and state management using useState hook.',
          learning_outcomes: '1. Understand React component structure\n2. Master JSX syntax and rendering\n3. Learn props and state management\n4. Build simple interactive components',
          recommendations: 'Students should practice building more components and understand the component lifecycle. Some students struggled with state management concepts.',
          venue: 'Room 101, ICT Building',
          scheduled_lecture_time: '10:00:00',
          average_rating: 4.2,
          rating_count: 15
        });
        
        setRatings([
          { id: 1, student_name: 'Alice Johnson', rating: 5, comment: 'Great explanation of React concepts!', created_at: '2024-10-01T14:30:00Z' },
          { id: 2, student_name: 'Bob Smith', rating: 4, comment: 'Good lecture but could use more examples', created_at: '2024-10-01T15:20:00Z' },
          { id: 3, student_name: 'Charlie Brown', rating: 3, comment: 'Some parts were confusing', created_at: '2024-10-02T09:15:00Z' }
        ]);
        
        setAverageRating(4.2);
        setLoading(false);
      }, 1000);
    } catch (error) {
      console.error('Error loading report details:', error);
      setLoading(false);
    }
  };

  const renderStars = (rating) => {
    return (
      <div className="star-rating">
        {[1, 2, 3, 4, 5].map(star => (
          <span 
            key={star} 
            className={star <= rating ? 'text-warning' : 'text-muted'}
          >
            ★
          </span>
        ))}
      </div>
    );
  };

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p>Loading report details...</p>
        </div>
      </div>
    );
  }

  if (!report) {
    return (
      <div className="container mt-4">
        <div className="alert alert-danger">Report not found</div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <button 
            className="btn btn-outline-secondary me-2"
            onClick={() => window.history.back()}
          >
            ← Back
          </button>
          <h3 className="d-inline-block mb-0">Report Details</h3>
        </div>
        <button 
          className="btn btn-success"
          onClick={() => window.location.href = `/student/rate/${reportId}`}
        >
          ⭐ Rate This Lecture
        </button>
      </div>

      <div className="row">
        {/* Report Details */}
        <div className="col-md-8">
          <div className="card mb-4">
            <div className="card-header">
              <h5 className="card-title mb-0">Teaching Report</h5>
            </div>
            <div className="card-body">
              <div className="row mb-3">
                <div className="col-md-6">
                  <strong>Course:</strong> {report.course_code} - {report.course_name}
                </div>
                <div className="col-md-6">
                  <strong>Lecturer:</strong> {report.lecturer_name}
                </div>
              </div>
              
              <div className="row mb-3">
                <div className="col-md-4">
                  <strong>Class:</strong> {report.class_name}
                </div>
                <div className="col-md-4">
                  <strong>Date:</strong> {new Date(report.date_of_lecture).toLocaleDateString()}
                </div>
                <div className="col-md-4">
                  <strong>Week:</strong> {report.week_of_reporting}
                </div>
              </div>

              <div className="row mb-3">
                <div className="col-md-6">
                  <strong>Venue:</strong> {report.venue || 'Not specified'}
                </div>
                <div className="col-md-6">
                  <strong>Time:</strong> {report.scheduled_lecture_time ? report.scheduled_lecture_time.substring(0, 5) : 'Not specified'}
                </div>
              </div>

              <div className="row mb-3">
                <div className="col-md-6">
                  <strong>Students Present:</strong> 
                  <span className={`badge ${report.actual_students_present > 35 ? 'bg-success' : 'bg-warning'} ms-2`}>
                    {report.actual_students_present}
                  </span>
                </div>
                <div className="col-md-6">
                  <strong>Average Rating:</strong>
                  <span className="ms-2">
                    {renderStars(report.average_rating)}
                    <small className="text-muted"> ({report.rating_count} ratings)</small>
                  </span>
                </div>
              </div>

              <div className="mb-3">
                <strong>Topic Taught:</strong>
                <p className="mt-1">{report.topic_taught}</p>
              </div>

              <div className="mb-3">
                <strong>Learning Outcomes:</strong>
                <pre className="mt-1 p-3 bg-light rounded" style={{ whiteSpace: 'pre-wrap' }}>
                  {report.learning_outcomes}
                </pre>
              </div>

              {report.recommendations && (
                <div className="mb-3">
                  <strong>Lecturer's Recommendations:</strong>
                  <p className="mt-1">{report.recommendations}</p>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Ratings Sidebar */}
        <div className="col-md-4">
          <div className="card">
            <div className="card-header">
              <h6 className="card-title mb-0">Student Ratings</h6>
            </div>
            <div className="card-body">
              {ratings.length === 0 ? (
                <p className="text-muted">No ratings yet. Be the first to rate!</p>
              ) : (
                <div>
                  {ratings.map(rating => (
                    <div key={rating.id} className="border-bottom pb-2 mb-2">
                      <div className="d-flex justify-content-between align-items-start">
                        <strong>{rating.student_name}</strong>
                        {renderStars(rating.rating)}
                      </div>
                      {rating.comment && (
                        <p className="mb-0 mt-1 small">{rating.comment}</p>
                      )}
                      <small className="text-muted">
                        {new Date(rating.created_at).toLocaleDateString()}
                      </small>
                    </div>
                  ))}
                </div>
              )}
              
              <button 
                className="btn btn-primary w-100 mt-3"
                onClick={() => window.location.href = `/student/rate/${reportId}`}
              >
                Add Your Rating
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ReportDetail;
