import React, { useState, useRef } from 'react';
import { authAPI } from '../../services/api';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const loginRef = useRef(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await authAPI.login(formData);
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('user', JSON.stringify(response.data.user));
      window.location.href = '/dashboard';
    } catch (error) {
      alert('Login failed: ' + (error.response?.data?.error || error.message));
    }
  };

  const scrollToLogin = () => {
    loginRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div className="login-landing">
      {/* Hero / Landing content */}
      <header className="hero text-center text-white d-flex align-items-center justify-content-center">
        <div className="hero-inner container">
          <h1 className="display-5">Welcome to LUCT Reporting</h1>
          <p className="lead mt-3">
            A simple reporting platform for lecturers and students to submit and review lecture reports,
            rate sessions, and export reports for academic review. Streamline attendance and reporting
            for better academic oversight.
          </p>
          <p className="mt-4">
            <button className="btn btn-light btn-lg" onClick={scrollToLogin}>Scroll to Login</button>
          </p>
          <div className="scroll-hint mt-3 text-muted">Scroll down to login or register</div>
        </div>
      </header>

      {/* Login card placed further down so user scrolls */}
      <section className="container login-section" ref={loginRef}>
        <div className="row justify-content-center">
          <div className="col-md-6">
            <div className="card">
              <div className="card-body">
                <h3 className="card-title text-center">LUCT Reporting System</h3>
                <h5 className="card-title text-center mb-4">Login</h5>
                <form onSubmit={handleSubmit}>
                  <div className="mb-3">
                    <label className="form-label">Email</label>
                    <input
                      type="email"
                      className="form-control"
                      value={formData.email}
                      onChange={(e) => setFormData({...formData, email: e.target.value})}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Password</label>
                    <input
                      type="password"
                      className="form-control"
                      value={formData.password}
                      onChange={(e) => setFormData({...formData, password: e.target.value})}
                      required
                    />
                  </div>
                  <button type="submit" className="btn btn-primary w-100">
                    Login
                  </button>
                </form>
                <p className="text-center mt-3">
                  Don't have an account? <a href="/register">Register here</a>
                </p>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Login;
