import React, { useState, useEffect } from 'react';
import { reportsAPI, searchAPI } from '../../services/api';
import ModuleSearch from '../common/ModuleSearch';
import ExportButton from '../common/ExportButton';
import ExportModal from '../common/ExportModal';
import { exportAPI } from '../../services/api';
import { getExportParams } from '../../utils/exportUtils';

const MyReports = () => {
  const [reports, setReports] = useState([]);
  const [filteredReports, setFilteredReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showExportModal, setShowExportModal] = useState(false);
  const user = JSON.parse(localStorage.getItem('user') || '{}');

  useEffect(() => {
    loadMyReports();
  }, []);

  const loadMyReports = async () => {
    try {
      // For now, we'll use mock data since we don't have the real API endpoint
      // const response = await reportsAPI.getMyReports(user.id);
      // setReports(response.data);
      
      // Mock data for testing
      setTimeout(() => {
        const mockReports = [
          {
            id: 1,
            week_of_reporting: 'Week 6',
            date_of_lecture: '2024-10-01',
            class_name: 'BIT-1A',
            course_name: 'Web Application Development',
            course_code: 'DIWA2110',
            actual_students_present: 38,
            topic_taught: 'Introduction to React and Component Architecture',
            learning_outcomes: 'Students should understand React components and props',
            recommendations: 'Need more practical examples for state management'
          },
          {
            id: 2,
            week_of_reporting: 'Week 5',
            date_of_lecture: '2024-09-24',
            class_name: 'BIT-1B',
            course_name: 'Web Application Development',
            course_code: 'DIWA2110',
            actual_students_present: 42,
            topic_taught: 'JavaScript ES6+ Features and Async/Await',
            learning_outcomes: 'Students should master modern JavaScript syntax',
            recommendations: null
          },
          {
            id: 3,
            week_of_reporting: 'Week 4',
            date_of_lecture: '2024-09-17',
            class_name: 'BIT-1A',
            course_name: 'Network Management',
            course_code: 'DINM2110',
            actual_students_present: 35,
            topic_taught: 'Network Protocols and TCP/IP Model',
            learning_outcomes: 'Understanding of network layers and protocols',
            recommendations: 'Students need more practice with subnetting'
          }
        ];
        setReports(mockReports);
        setFilteredReports(mockReports);
        setLoading(false);
      }, 1000);
    } catch (error) {
      console.error('Error loading reports:', error);
      setLoading(false);
    }
  };

  const handleSearch = async (query) => {
    if (!query.trim()) {
      setFilteredReports(reports);
      return;
    }

    try {
      const response = await searchAPI.reports(query);
      setFilteredReports(response.data);
    } catch (error) {
      console.error('Search error:', error);
      // Fallback to client-side filtering
      const filtered = reports.filter(report => 
        report.topic_taught.toLowerCase().includes(query.toLowerCase()) ||
        report.course_name.toLowerCase().includes(query.toLowerCase()) ||
        report.course_code.toLowerCase().includes(query.toLowerCase()) ||
        report.week_of_reporting.toLowerCase().includes(query.toLowerCase())
      );
      setFilteredReports(filtered);
    }
  };

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p>Loading your reports...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h3>My Teaching Reports</h3>
        <div className="d-flex gap-2">
          <ModuleSearch 
            onSearch={handleSearch}
            placeholder="Search reports by topic, course..."
            size="sm"
          />
          <ExportButton
            exportFunction={() => exportAPI.reports(getExportParams(user))}
            filename="my-teaching-reports"
            buttonText="Export My Reports"
            variant="outline-success"
          />
          <button 
            className="btn btn-primary"
            onClick={() => window.location.href = '/lecturer/report-form'}
          >
            + New Report
          </button>
        </div>
      </div>

      {filteredReports.length === 0 ? (
        <div className="card">
          <div className="card-body text-center">
            <h5>No Reports Yet</h5>
            <p>You haven't submitted any teaching reports yet.</p>
            <button 
              className="btn btn-success"
              onClick={() => window.location.href = '/lecturer/report-form'}
            >
              Create Your First Report
            </button>
          </div>
        </div>
      ) : (
        <div className="card">
          <div className="card-body">
            <div className="table-responsive">
              <table className="table table-striped">
                <thead>
                  <tr>
                    <th>Week</th>
                    <th>Date</th>
                    <th>Class</th>
                    <th>Course</th>
                    <th>Students Present</th>
                    <th>Topic</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredReports.map(report => (
                    <tr key={report.id}>
                      <td>{report.week_of_reporting}</td>
                      <td>{new Date(report.date_of_lecture).toLocaleDateString()}</td>
                      <td>{report.class_name}</td>
                      <td>{report.course_code} - {report.course_name}</td>
                      <td>
                        <span className={`badge ${report.actual_students_present > 35 ? 'bg-success' : 'bg-warning'}`}>
                          {report.actual_students_present}
                        </span>
                      </td>
                      <td>
                        <small>{report.topic_taught.substring(0, 50)}...</small>
                      </td>
                      <td>
                        <button 
                          className="btn btn-sm btn-outline-primary me-1"
                          onClick={() => alert(`Viewing report ${report.id}`)}
                        >
                          View
                        </button>
                        <button 
                          className="btn btn-sm btn-outline-secondary"
                          onClick={() => alert(`Editing report ${report.id}`)}
                        >
                          Edit
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}

      {/* Report Statistics */}
      <div className="row mt-4">
        <div className="col-md-3">
          <div className="card text-white bg-primary">
            <div className="card-body">
              <h5 className="card-title">{filteredReports.length}</h5>
              <p className="card-text">Total Reports</p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card text-white bg-success">
            <div className="card-body">
              <h5 className="card-title">
                {filteredReports.reduce((acc, report) => acc + report.actual_students_present, 0)}
              </h5>
              <p className="card-text">Total Students Taught</p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card text-white bg-info">
            <div className="card-body">
              <h5 className="card-title">
                {new Set(filteredReports.map(r => r.course_code)).size}
              </h5>
              <p className="card-text">Courses Covered</p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card text-white bg-warning">
            <div className="card-body">
              <h5 className="card-title">
                {new Set(filteredReports.map(r => r.class_name)).size}
              </h5>
              <p className="card-text">Classes Taught</p>
            </div>
          </div>
        </div>
      </div>

      {/* Export Modal */}
      <ExportModal
        show={showExportModal}
        onClose={() => setShowExportModal(false)}
        user={user}
        exportType="reports"
      />
    </div>
  );
};

export default MyReports;
