import React, { useState, useEffect, useRef } from 'react';
import { searchAPI } from '../../services/api';

const GlobalSearch = () => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const searchRef = useRef(null);
  const user = JSON.parse(localStorage.getItem('user') || '{}');

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchRef.current && !searchRef.current.contains(event.target)) {
        setShowResults(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const performSearch = async (searchQuery) => {
    if (!searchQuery.trim()) {
      setResults(null);
      setShowResults(false);
      return;
    }

    setLoading(true);
    try {
      const response = await searchAPI.global(searchQuery, user.role, user.id);
      setResults(response.data);
      setShowResults(true);
    } catch (error) {
      console.error('Search error:', error);
      setResults({ 
        query: searchQuery, 
        results: {}, 
        total_results: 0,
        error: 'Search failed' 
      });
      setShowResults(true);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e) => {
    const value = e.target.value;
    setQuery(value);
    
    // Debounce search
    clearTimeout(window.searchTimeout);
    window.searchTimeout = setTimeout(() => {
      performSearch(value);
    }, 300);
  };

  const handleResultClick = (type, item) => {
    setShowResults(false);
    setQuery('');
    
    // Navigate based on result type
    switch (type) {
      case 'courses':
        alert(`Navigate to course: ${item.course_code}`);
        break;
      case 'reports':
        if (user.role === 'student') {
          window.location.href = `/student/report/${item.id}`;
        } else {
          alert(`Show report: ${item.topic_taught}`);
        }
        break;
      case 'users':
        alert(`Show user: ${item.name}`);
        break;
      case 'classes':
        alert(`Show class: ${item.class_name}`);
        break;
      default:
        break;
    }
  };

  return (
    <div className="position-relative" ref={searchRef}>
      <div className="input-group">
        <input
          type="text"
          className="form-control"
          placeholder="Search courses, reports, lecturers..."
          value={query}
          onChange={handleSearch}
          onFocus={() => query && setShowResults(true)}
        />
        <button className="btn btn-outline-secondary" type="button">
          {loading ? (
            <div className="spinner-border spinner-border-sm" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          ) : (
            'üîç'
          )}
        </button>
      </div>

      {/* Search Results Dropdown */}
      {showResults && results && (
        <div className="search-results-dropdown position-absolute top-100 start-0 end-0 mt-1 bg-white border rounded shadow-lg z-3">
          <div className="p-2 border-bottom">
            <small className="text-muted">
              Found {results.total_results} results for "{results.query}"
            </small>
            <button 
              className="btn btn-sm btn-outline-secondary float-end"
              onClick={() => setShowResults(false)}
            >
              ‚úï
            </button>
          </div>
          
          <div className="search-results-body" style={{ maxHeight: '400px', overflowY: 'auto' }}>
            {results.total_results === 0 ? (
              <div className="p-3 text-center text-muted">
                No results found for "{results.query}"
              </div>
            ) : (
              <>
                {/* Courses Results */}
                {results.results.courses && results.results.courses.length > 0 && (
                  <div className="search-category">
                    <div className="search-category-header p-2 bg-light">
                      <strong>üìö Courses</strong>
                    </div>
                    {results.results.courses.map(course => (
                      <div
                        key={`course-${course.id}`}
                        className="search-result-item p-2 border-bottom"
                        onClick={() => handleResultClick('courses', course)}
                        style={{ cursor: 'pointer' }}
                      >
                        <div className="fw-bold">{course.course_code}</div>
                        <div className="small text-muted">{course.course_name}</div>
                        <span className="badge bg-secondary">{course.stream}</span>
                      </div>
                    ))}
                  </div>
                )}

                {/* Reports Results */}
                {results.results.reports && results.results.reports.length > 0 && (
                  <div className="search-category">
                    <div className="search-category-header p-2 bg-light">
                      <strong>üìù Reports</strong>
                    </div>
                    {results.results.reports.map(report => (
                      <div
                        key={`report-${report.id}`}
                        className="search-result-item p-2 border-bottom"
                        onClick={() => handleResultClick('reports', report)}
                        style={{ cursor: 'pointer' }}
                      >
                        <div className="fw-bold">{report.course_code || 'Teaching Report'}</div>
                        <div className="small text-muted">{report.topic_taught?.substring(0, 60)}...</div>
                        <div className="small">
                          {new Date(report.date_of_lecture).toLocaleDateString()}
                          {report.lecturer_name && ` ‚Ä¢ ${report.lecturer_name}`}
                        </div>
                      </div>
                    ))}
                  </div>
                )}

                {/* Users Results */}
                {results.results.users && results.results.users.length > 0 && (
                  <div className="search-category">
                    <div className="search-category-header p-2 bg-light">
                      <strong>üë®‚Äçüè´ Lecturers & Staff</strong>
                    </div>
                    {results.results.users.map(user => (
                      <div
                        key={`user-${user.id}`}
                        className="search-result-item p-2 border-bottom"
                        onClick={() => handleResultClick('users', user)}
                        style={{ cursor: 'pointer' }}
                      >
                        <div className="fw-bold">{user.name}</div>
                        <div className="small text-muted">{user.email}</div>
                        <span className={`badge ${
                          user.role === 'principal_lecturer' ? 'bg-warning' : 
                          user.role === 'program_leader' ? 'bg-success' : 'bg-primary'
                        }`}>
                          {user.role}
                        </span>
                      </div>
                    ))}
                  </div>
                )}

                {/* Classes Results */}
                {results.results.classes && results.results.classes.length > 0 && (
                  <div className="search-category">
                    <div className="search-category-header p-2 bg-light">
                      <strong>üè´ Classes</strong>
                    </div>
                    {results.results.classes.map(classItem => (
                      <div
                        key={`class-${classItem.id}`}
                        className="search-result-item p-2 border-bottom"
                        onClick={() => handleResultClick('classes', classItem)}
                        style={{ cursor: 'pointer' }}
                      >
                        <div className="fw-bold">{classItem.class_name}</div>
                        <div className="small text-muted">{classItem.faculty}</div>
                        {classItem.venue && <span className="badge bg-info">{classItem.venue}</span>}
                      </div>
                    ))}
                  </div>
                )}
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default GlobalSearch;
