import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { ratingsAPI, reportsAPI } from '../../services/api';

const RatingForm = () => {
  const { reportId } = useParams();
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const [report, setReport] = useState(null);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');
  const [hoverRating, setHoverRating] = useState(0);
  const [submitting, setSubmitting] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadReportInfo();
  }, [reportId]);

  const loadReportInfo = async () => {
    try {
      // Mock data for testing
      setTimeout(() => {
        setReport({
          id: parseInt(reportId),
          course_code: 'DIWA2110',
          course_name: 'Web Application Development',
          lecturer_name: 'Dr. John Smith',
          topic_taught: 'Introduction to React and Component Architecture'
        });
        setLoading(false);
      }, 500);
    } catch (error) {
      console.error('Error loading report info:', error);
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (rating === 0) {
      alert('Please select a rating');
      return;
    }

    setSubmitting(true);

    try {
      const ratingData = {
        student_id: user.id,
        report_id: parseInt(reportId),
        rating: rating,
        comment: comment
      };

      // In real app: await ratingsAPI.submit(ratingData);
      
      // Mock submission
      setTimeout(() => {
        alert('✅ Rating submitted successfully!');
        window.location.href = `/student/report/${reportId}`;
      }, 1000);

    } catch (error) {
      console.error('Error submitting rating:', error);
      alert('Error submitting rating: ' + (error.response?.data?.error || error.message));
      setSubmitting(false);
    }
  };

  const renderStars = () => {
    return (
      <div className="star-rating mb-3">
        {[1, 2, 3, 4, 5].map(star => (
          <button
            key={star}
            type="button"
            className={`btn btn-lg ${star <= (hoverRating || rating) ? 'text-warning' : 'text-muted'}`}
            onClick={() => setRating(star)}
            onMouseEnter={() => setHoverRating(star)}
            onMouseLeave={() => setHoverRating(0)}
            style={{ border: 'none', background: 'none', fontSize: '2rem' }}
          >
            ★
          </button>
        ))}
      </div>
    );
  };

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p>Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <div className="row justify-content-center">
        <div className="col-md-8">
          <div className="card">
            <div className="card-header">
              <button 
                className="btn btn-outline-secondary btn-sm"
                onClick={() => window.history.back()}
              >
                ← Back
              </button>
              <h4 className="card-title text-center mb-0">Rate This Lecture</h4>
            </div>
            <div className="card-body">
              {report && (
                <div className="text-center mb-4">
                  <h5>{report.course_code} - {report.course_name}</h5>
                  <p className="text-muted mb-1">Lecturer: {report.lecturer_name}</p>
                  <p className="text-muted">Topic: {report.topic_taught}</p>
                </div>
              )}

              <form onSubmit={handleSubmit}>
                <div className="text-center mb-4">
                  <h6>How would you rate this lecture?</h6>
                  {renderStars()}
                  <div>
                    {rating > 0 && (
                      <span className="text-muted">
                        {rating === 1 && 'Poor'}
                        {rating === 2 && 'Fair'}
                        {rating === 3 && 'Good'}
                        {rating === 4 && 'Very Good'}
                        {rating === 5 && 'Excellent'}
                      </span>
                    )}
                  </div>
                </div>

                <div className="mb-3">
                  <label htmlFor="comment" className="form-label">
                    Additional Comments (Optional)
                  </label>
                  <textarea
                    id="comment"
                    className="form-control"
                    rows="4"
                    placeholder="Share your thoughts about the lecture..."
                    value={comment}
                    onChange={(e) => setComment(e.target.value)}
                  ></textarea>
                </div>

                <div className="d-grid">
                  <button 
                    type="submit" 
                    className="btn btn-primary btn-lg"
                    disabled={submitting || rating === 0}
                  >
                    {submitting ? 'Submitting...' : 'Submit Rating'}
                  </button>
                </div>
              </form>

              <div className="mt-4 p-3 bg-light rounded">
                <h6>Rating Guidelines:</h6>
                <ul className="small mb-0">
                  <li>⭐ (1) - Poor: Lecture was unclear or unhelpful</li>
                  <li>⭐⭐ (2) - Fair: Basic understanding but needs improvement</li>
                  <li>⭐⭐⭐ (3) - Good: Clear and informative</li>
                  <li>⭐⭐⭐⭐ (4) - Very Good: Engaging and educational</li>
                  <li>⭐⭐⭐⭐⭐ (5) - Excellent: Outstanding teaching and content</li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RatingForm;
