import React, { useState, useEffect } from 'react';
import { reportsAPI, classesAPI, coursesAPI } from '../../services/api';

const ReportForm = () => {
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const [formData, setFormData] = useState({
    lecturer_id: user.id,
    class_id: '',
    course_id: '',
    week_of_reporting: '',
    date_of_lecture: '',
    actual_students_present: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: '',
    venue: '',
    scheduled_lecture_time: ''
  });
  
  const [classes, setClasses] = useState([]);
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  // Load classes and courses on component mount
  useEffect(() => {
    loadFormData();
  }, []);

  const loadFormData = async () => {
    try {
      // In a real app, you'd have API endpoints for these
      // For now, we'll use mock data or you can create the endpoints
      const classesResponse = await classesAPI.getAll();
      const coursesResponse = await coursesAPI.getAll();
      
      setClasses(classesResponse.data || [
        { id: 1, class_name: 'BIT-1A', faculty: 'ICT', total_registered_students: 45, venue: 'Room 101' },
        { id: 2, class_name: 'BIT-1B', faculty: 'ICT', total_registered_students: 42, venue: 'Room 102' },
        { id: 3, class_name: 'DIT-1A', faculty: 'ICT', total_registered_students: 38, venue: 'Room 201' },
        { id: 4, class_name: 'DIT-1B', faculty: 'ICT', total_registered_students: 40, venue: 'Room 202' }
      ]);
      
      setCourses(coursesResponse.data || [
        { id: 1, course_code: 'DIWA2110', course_name: 'Web Application Development' },
        { id: 2, course_code: 'DINM2110', course_name: 'Network Management' },
        { id: 3, course_code: 'DIDS2110', course_name: 'Database Systems' },
        { id: 4, course_code: 'DIPR2110', course_name: 'Programming Fundamentals' }
      ]);
    } catch (error) {
      console.error('Error loading form data:', error);
      // Use mock data if API fails
      setClasses([
        { id: 1, class_name: 'BIT-1A', faculty: 'ICT', total_registered_students: 45, venue: 'Room 101' },
        { id: 2, class_name: 'BIT-1B', faculty: 'ICT', total_registered_students: 42, venue: 'Room 102' }
      ]);
      setCourses([
        { id: 1, course_code: 'DIWA2110', course_name: 'Web Application Development' },
        { id: 2, course_code: 'DINM2110', course_name: 'Network Management' }
      ]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      const response = await reportsAPI.create(formData);
      setMessage('✅ Report submitted successfully!');
      
      // Reset form
      setFormData({
        lecturer_id: user.id,
        class_id: '',
        course_id: '',
        week_of_reporting: '',
        date_of_lecture: '',
        actual_students_present: '',
        topic_taught: '',
        learning_outcomes: '',
        recommendations: '',
        venue: '',
        scheduled_lecture_time: ''
      });
    } catch (error) {
      console.error('Error submitting report:', error);
      setMessage('❌ Error submitting report: ' + (error.response?.data?.error || error.message));
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const getTotalRegisteredStudents = () => {
    const selectedClass = classes.find(cls => cls.id == formData.class_id);
    return selectedClass ? selectedClass.total_registered_students : 0;
  };

  return (
    <div className="container mt-4">
      <div className="card">
        <div className="card-header">
          <h4>Lecturer Reporting Form</h4>
          <p className="mb-0">Submit your teaching report for the week</p>
        </div>
        <div className="card-body">
          {message && (
            <div className={`alert ${message.includes('✅') ? 'alert-success' : 'alert-danger'}`}>
              {message}
            </div>
          )}

          <form onSubmit={handleSubmit}>
            <div className="row">
              {/* Class Information */}
              <div className="col-md-6">
                <div className="mb-3">
                  <label className="form-label">Class *</label>
                  <select
                    name="class_id"
                    className="form-control"
                    value={formData.class_id}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select Class</option>
                    {classes.map(cls => (
                      <option key={cls.id} value={cls.id}>
                        {cls.class_name} - {cls.faculty} ({cls.total_registered_students} students)
                      </option>
                    ))}
                  </select>
                </div>
              </div>

              <div className="col-md-6">
                <div className="mb-3">
                  <label className="form-label">Course *</label>
                  <select
                    name="course_id"
                    className="form-control"
                    value={formData.course_id}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select Course</option>
                    {courses.map(course => (
                      <option key={course.id} value={course.id}>
                        {course.course_code} - {course.course_name}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
            </div>

            <div className="row">
              <div className="col-md-4">
                <div className="mb-3">
                  <label className="form-label">Week of Reporting *</label>
                  <input
                    type="text"
                    name="week_of_reporting"
                    className="form-control"
                    placeholder="e.g., Week 6"
                    value={formData.week_of_reporting}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>

              <div className="col-md-4">
                <div className="mb-3">
                  <label className="form-label">Date of Lecture *</label>
                  <input
                    type="date"
                    name="date_of_lecture"
                    className="form-control"
                    value={formData.date_of_lecture}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>

              <div className="col-md-4">
                <div className="mb-3">
                  <label className="form-label">Scheduled Time</label>
                  <input
                    type="time"
                    name="scheduled_lecture_time"
                    className="form-control"
                    value={formData.scheduled_lecture_time}
                    onChange={handleChange}
                  />
                </div>
              </div>
            </div>

            <div className="row">
              <div className="col-md-6">
                <div className="mb-3">
                  <label className="form-label">Total Registered Students</label>
                  <input
                    type="number"
                    className="form-control"
                    value={getTotalRegisteredStudents()}
                    disabled
                  />
                  <small className="form-text text-muted">
                    Auto-filled based on selected class
                  </small>
                </div>
              </div>

              <div className="col-md-6">
                <div className="mb-3">
                  <label className="form-label">Actual Students Present *</label>
                  <input
                    type="number"
                    name="actual_students_present"
                    className="form-control"
                    placeholder="Enter number of students present"
                    value={formData.actual_students_present}
                    onChange={handleChange}
                    max={getTotalRegisteredStudents()}
                    required
                  />
                </div>
              </div>
            </div>

            <div className="mb-3">
              <label className="form-label">Venue</label>
              <input
                type="text"
                name="venue"
                className="form-control"
                placeholder="e.g., Room 101, Building A"
                value={formData.venue}
                onChange={handleChange}
              />
            </div>

            <div className="mb-3">
              <label className="form-label">Topic Taught *</label>
              <textarea
                name="topic_taught"
                className="form-control"
                rows="3"
                placeholder="Describe the topic you taught in this lecture..."
                value={formData.topic_taught}
                onChange={handleChange}
                required
              ></textarea>
            </div>

            <div className="mb-3">
              <label className="form-label">Learning Outcomes *</label>
              <textarea
                name="learning_outcomes"
                className="form-control"
                rows="3"
                placeholder="What should students be able to do after this lecture?"
                value={formData.learning_outcomes}
                onChange={handleChange}
                required
              ></textarea>
            </div>

            <div className="mb-3">
              <label className="form-label">Lecturer's Recommendations</label>
              <textarea
                name="recommendations"
                className="form-control"
                rows="3"
                placeholder="Any recommendations for improvement, student issues, etc."
                value={formData.recommendations}
                onChange={handleChange}
              ></textarea>
            </div>

            <div className="d-grid">
              <button 
                type="submit" 
                className="btn btn-primary btn-lg"
                disabled={loading}
              >
                {loading ? 'Submitting Report...' : 'Submit Teaching Report'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ReportForm;
