import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
});

// Add token to requests
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

export const authAPI = {
  register: (userData) => api.post('/auth/register', userData),
  login: (credentials) => api.post('/auth/login', credentials),
};

// Add to your existing authAPI and reportsAPI objects:

export const classesAPI = {
  getAll: () => api.get('/classes'),
};

export const coursesAPI = {
  getAll: () => api.get('/courses'),
};

// Add to your existing API exports:

export const ratingsAPI = {
  submit: (ratingData) => api.post('/ratings', ratingData),
  getByReport: (reportId) => api.get(`/ratings/report/${reportId}`),
  getByStudent: (studentId) => api.get(`/ratings/student/${studentId}`),
};

export const reportsAPI = {
  getAll: () => api.get('/reports'),
  getMyReports: (lecturerId) => api.get(`/reports/my-reports?lecturer_id=${lecturerId}`),
  getById: (id) => api.get(`/reports/${id}`),
  getStudentReports: (faculty) => api.get(`/reports/student-reports?faculty=${faculty}`),
  getStudentReport: (id) => api.get(`/reports/student/${id}`),
  create: (reportData) => api.post('/reports', reportData),
};

// Add to your existing API exports:

export const principalAPI = {
  getCourses: (faculty) => api.get(`/principal/courses?faculty=${faculty}`),
  getReports: (faculty) => api.get(`/principal/reports?faculty=${faculty}`),
  getStatistics: (faculty) => api.get(`/principal/statistics?faculty=${faculty}`),
  getLecturers: (faculty) => api.get(`/principal/lecturers?faculty=${faculty}`),
  addFeedback: (reportId, feedback) => api.post(`/principal/reports/${reportId}/feedback`, { feedback }),
};

export default api;
