import React, { useState, useEffect } from 'react';
import { programLeaderAPI } from '../../services/api';
import ExportButton from '../common/ExportButton';
import { exportAPI } from '../../services/api';

const ProgramLeaderDashboard = () => {
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const [activeTab, setActiveTab] = useState('overview');
  const [statistics, setStatistics] = useState(null);
  const [courses, setCourses] = useState([]);
  const [reports, setReports] = useState([]);
  const [lecturers, setLecturers] = useState([]);
  const [classes, setClasses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddCourse, setShowAddCourse] = useState(false);
  const [newCourse, setNewCourse] = useState({
    course_code: '',
    course_name: '',
    stream: 'ICT',
    lecturer_id: ''
  });

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      
      // Mock data for testing
      setTimeout(() => {
        setStatistics({
          courses: { total_courses: 15, assigned_courses: 12, total_streams: 3 },
          lecturers: { total_lecturers: 8, avg_courses_per_lecturer: 1.5 },
          reports: { total_reports: 85, avg_attendance: 37.2, active_lecturers: 8 },
          ratings: { avg_rating: 4.2, total_ratings: 250 }
        });
        
        setCourses([
          { id: 1, course_code: 'DIWA2110', course_name: 'Web Application Development', stream: 'ICT', lecturer_name: 'Dr. John Smith', report_count: 12, average_rating: 4.5 },
          { id: 2, course_code: 'DINM2110', course_name: 'Network Management', stream: 'ICT', lecturer_name: 'Prof. Sarah Johnson', report_count: 10, average_rating: 4.2 },
          { id: 3, course_code: 'BIBM2110', course_name: 'Business Intelligence', stream: 'Business', lecturer_name: 'Dr. Robert Chen', report_count: 8, average_rating: 4.3 },
          { id: 4, course_code: 'DIPR2110', course_name: 'Programming Fundamentals', stream: 'ICT', lecturer_name: null, report_count: 0, average_rating: null }
        ]);
        
        setReports([
          { 
            id: 1, 
            course_code: 'DIWA2110', 
            course_name: 'Web Application Development', 
            lecturer_name: 'Dr. John Smith',
            principal_name: 'Prof. David Wilson',
            class_name: 'BIT-1A',
            date_of_lecture: '2024-10-01',
            actual_students_present: 38,
            topic_taught: 'Introduction to React and Component Architecture',
            average_rating: 4.2,
            principal_feedback: 'Good coverage of React fundamentals. Consider adding more practical examples.'
          },
          { 
            id: 2, 
            course_code: 'BIBM2110', 
            course_name: 'Business Intelligence', 
            lecturer_name: 'Dr. Robert Chen',
            principal_name: 'Prof. Lisa Wang',
            class_name: 'BBIT-1A',
            date_of_lecture: '2024-09-28',
            actual_students_present: 35,
            topic_taught: 'Data Warehousing and ETL Processes',
            average_rating: 4.4,
            principal_feedback: 'Excellent real-world examples. Students engaged well with the case studies.'
          }
        ]);
        
        setLecturers([
          { id: 1, name: 'Dr. John Smith', email: 'john.smith@luct.ac.ls', faculty: 'ICT', assigned_courses: 2, total_reports: 12, average_rating: 4.5 },
          { id: 2, name: 'Prof. Sarah Johnson', email: 'sarah.johnson@luct.ac.ls', faculty: 'ICT', assigned_courses: 1, total_reports: 10, average_rating: 4.2 },
          { id: 3, name: 'Dr. Robert Chen', email: 'robert.chen@luct.ac.ls', faculty: 'Business', assigned_courses: 2, total_reports: 8, average_rating: 4.3 },
          { id: 4, name: 'Prof. Emily Brown', email: 'emily.brown@luct.ac.ls', faculty: 'ICT', assigned_courses: 0, total_reports: 0, average_rating: null }
        ]);
        
        setClasses([
          { id: 1, class_name: 'BIT-1A', faculty: 'ICT', total_registered_students: 45, venue: 'Room 101', report_count: 12, avg_attendance: 38.5 },
          { id: 2, class_name: 'BIT-1B', faculty: 'ICT', total_registered_students: 42, venue: 'Room 102', report_count: 10, avg_attendance: 39.2 },
          { id: 3, class_name: 'BBIT-1A', faculty: 'Business', total_registered_students: 38, venue: 'Room 201', report_count: 8, avg_attendance: 35.8 }
        ]);
        
        setLoading(false);
      }, 1000);
      
    } catch (error) {
      console.error('Error loading PL dashboard:', error);
      setLoading(false);
    }
  };

  const handleAddCourse = async (e) => {
    e.preventDefault();
    try {
      alert(`Course ${newCourse.course_code} created successfully!`);
      setShowAddCourse(false);
      setNewCourse({ course_code: '', course_name: '', stream: 'ICT', lecturer_id: '' });
    } catch (error) {
      alert('Error creating course: ' + error.message);
    }
  };

  const handleAssignLecturer = async (courseId, lecturerId) => {
    try {
      alert('Lecturer assigned successfully!');
    } catch (error) {
      alert('Error assigning lecturer: ' + error.message);
    }
  };

  const renderStars = (rating) => {
    if (!rating) return <span className="text-muted">No ratings</span>;
    
    return (
      <div className="star-rating">
        {[1, 2, 3, 4, 5].map(star => (
          <span 
            key={star} 
            className={star <= rating ? 'text-warning' : 'text-muted'}
          >
            
          </span>
        ))}
        <small className="text-muted ms-1">({rating?.toFixed(1)})</small>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p>Loading Program Leader Dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <h3>Program Leader Dashboard</h3>
          <p className="text-muted mb-0">Program Management & Oversight</p>
        </div>
        <span className="badge bg-success fs-6">Program Leader</span>
      </div>

      {/* Navigation Tabs */}
      <ul className="nav nav-tabs mb-4">
        <li className="nav-item">
          <button 
            className={`nav-link ${activeTab === 'overview' ? 'active' : ''}`}
            onClick={() => setActiveTab('overview')}
          >
             Overview
          </button>
        </li>
        <li className="nav-item">
          <button 
            className={`nav-link ${activeTab === 'courses' ? 'active' : ''}`}
            onClick={() => setActiveTab('courses')}
          >
             Courses
          </button>
        </li>
        <li className="nav-item">
          <button 
            className={`nav-link ${activeTab === 'reports' ? 'active' : ''}`}
            onClick={() => setActiveTab('reports')}
          >
             Reports
          </button>
        </li>
        <li className="nav-item">
          <button 
            className={`nav-link ${activeTab === 'lecturers' ? 'active' : ''}`}
            onClick={() => setActiveTab('lecturers')}
          >
             Lecturers
          </button>
        </li>
        <li className="nav-item">
          <button 
            className={`nav-link ${activeTab === 'classes' ? 'active' : ''}`}
            onClick={() => setActiveTab('classes')}
          >
             Classes
          </button>
        </li>
        <li className="nav-item">
          <button 
            className={`nav-link ${activeTab === 'monitoring' ? 'active' : ''}`}
            onClick={() => setActiveTab('monitoring')}
          >
             Monitoring
          </button>
        </li>
      </ul>

      {/* Overview Tab */}
      {activeTab === 'overview' && (
        <div>
          {/* Statistics Cards */}
          <div className="row mb-4">
            <div className="col-md-3">
              <div className="card text-white bg-primary">
                <div className="card-body">
                  <h5 className="card-title">{statistics?.courses?.total_courses || 0}</h5>
                  <p className="card-text">Total Courses</p>
                </div>
              </div>
            </div>
            <div className="col-md-3">
              <div className="card text-white bg-success">
                <div className="card-body">
                  <h5 className="card-title">{statistics?.lecturers?.total_lecturers || 0}</h5>
                  <p className="card-text">Lecturers</p>
                </div>
              </div>
            </div>
            <div className="col-md-3">
              <div className="card text-white bg-info">
                <div className="card-body">
                  <h5 className="card-title">{statistics?.reports?.total_reports || 0}</h5>
                  <p className="card-text">Teaching Reports</p>
                </div>
              </div>
            </div>
            <div className="col-md-3">
              <div className="card text-white bg-warning">
                <div className="card-body">
                  <h5 className="card-title">{statistics?.ratings?.avg_rating?.toFixed(1) || 'N/A'}</h5>
                  <p className="card-text">Avg Rating</p>
                </div>
              </div>
            </div>
          </div>

          {/* Program Statistics */}
          <div className="row">
            <div className="col-md-6">
              <div className="card">
                <div className="card-header">
                  <h6 className="card-title mb-0">Program Statistics</h6>
                </div>
                <div className="card-body">
                  <p><strong>Assigned Courses:</strong> {statistics?.courses?.assigned_courses || 0} / {statistics?.courses?.total_courses || 0}</p>
                  <p><strong>Active Lecturers:</strong> {statistics?.reports?.active_lecturers || 0} / {statistics?.lecturers?.total_lecturers || 0}</p>
                  <p><strong>Average Courses per Lecturer:</strong> {statistics?.lecturers?.avg_courses_per_lecturer?.toFixed(1) || 0}</p>
                  <p><strong>Total Streams:</strong> {statistics?.courses?.total_streams || 0}</p>
                </div>
              </div>
            </div>
            <div className="col-md-6">
              <div className="card">
                <div className="card-header">
                  <h6 className="card-title mb-0">Performance Overview</h6>
                </div>
                <div className="card-body">
                  <p><strong>Average Attendance:</strong> {statistics?.reports?.avg_attendance?.toFixed(1) || 0} students</p>
                  <p><strong>Student Satisfaction:</strong> {statistics?.ratings?.avg_rating?.toFixed(1) || 'N/A'} / 5.0</p>
                  <p><strong>Total Student Ratings:</strong> {statistics?.ratings?.total_ratings || 0}</p>
                  <p><strong>Program Health:</strong> <span className="badge bg-success">Excellent</span></p>
                </div>
              </div>
            </div>
          </div>

          {/* Data Export Section */}
          <div className="card mt-4">
            <div className="card-header">
              <h6 className="card-title mb-0">Program Data Export</h6>
            </div>
            <div className="card-body">
              <div className="d-flex gap-2 flex-wrap">
                <ExportButton
                  exportFunction={exportAPI.reports}
                  filename="program-reports"
                  buttonText="Export All Reports"
                  variant="outline-success"
                />
                <ExportButton
                  exportFunction={exportAPI.courses}
                  filename="program-courses"
                  buttonText="Export Courses"
                  variant="outline-primary"
                />
                <ExportButton
                  exportFunction={exportAPI.ratings}
                  filename="program-ratings"
                  buttonText="Export Ratings"
                  variant="outline-info"
                />
              </div>
              <small className="text-muted mt-2 d-block">
                Export comprehensive program data for analysis and reporting.
              </small>
            </div>
          </div>
        </div>
      )}

      {/* Courses Tab */}
      {activeTab === 'courses' && (
        <div>
          <div className="d-flex justify-content-between align-items-center mb-3">
            <h5>Course Management</h5>
            <div className="d-flex gap-2">
              <ExportButton
                exportFunction={exportAPI.courses}
                filename="program-courses"
                buttonText="Export Courses"
                variant="success"
                size="sm"
              />
              <button 
                className="btn btn-success"
                onClick={() => setShowAddCourse(true)}
              >
                + Add New Course
              </button>
            </div>
          </div>

          {/* Add Course Modal */}
          {showAddCourse && (
            <div className="card mb-4">
              <div className="card-header">
                <h6 className="card-title mb-0">Add New Course</h6>
              </div>
              <div className="card-body">
                <form onSubmit={handleAddCourse}>
                  <div className="row">
                    <div className="col-md-3">
                      <div className="mb-3">
                        <label className="form-label">Course Code *</label>
                        <input
                          type="text"
                          className="form-control"
                          value={newCourse.course_code}
                          onChange={(e) => setNewCourse({...newCourse, course_code: e.target.value})}
                          required
                          placeholder="e.g., DIWA2110"
                        />
                      </div>
                    </div>
                    <div className="col-md-5">
                      <div className="mb-3">
                        <label className="form-label">Course Name *</label>
                        <input
                          type="text"
                          className="form-control"
                          value={newCourse.course_name}
                          onChange={(e) => setNewCourse({...newCourse, course_name: e.target.value})}
                          required
                          placeholder="e.g., Web Application Development"
                        />
                      </div>
                    </div>
                    <div className="col-md-2">
                      <div className="mb-3">
                        <label className="form-label">Stream *</label>
                        <select
                          className="form-control"
                          value={newCourse.stream}
                          onChange={(e) => setNewCourse({...newCourse, stream: e.target.value})}
                          required
                        >
                          <option value="ICT">ICT</option>
                          <option value="Business">Business</option>
                          <option value="Engineering">Engineering</option>
                        </select>
                      </div>
                    </div>
                    <div className="col-md-2">
                      <div className="mb-3">
                        <label className="form-label">Lecturer</label>
                        <select
                          className="form-control"
                          value={newCourse.lecturer_id}
                          onChange={(e) => setNewCourse({...newCourse, lecturer_id: e.target.value})}
                        >
                          <option value="">Not Assigned</option>
                          {lecturers.map(lecturer => (
                            <option key={lecturer.id} value={lecturer.id}>
                              {lecturer.name}
                            </option>
                          ))}
                        </select>
                      </div>
                    </div>
                  </div>
                  <div className="d-flex gap-2">
                    <button type="submit" className="btn btn-primary">
                      Create Course
                    </button>
                    <button 
                      type="button" 
                      className="btn btn-secondary"
                      onClick={() => setShowAddCourse(false)}
                    >
                      Cancel
                    </button>
                  </div>
                </form>
              </div>
            </div>
          )}

          <div className="card">
            <div className="card-body">
              <div className="table-responsive">
                <table className="table table-striped">
                  <thead>
                    <tr>
                      <th>Course Code</th>
                      <th>Course Name</th>
                      <th>Stream</th>
                      <th>Lecturer</th>
                      <th>Reports</th>
                      <th>Rating</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {courses.map(course => (
                      <tr key={course.id}>
                        <td><strong>{course.course_code}</strong></td>
                        <td>{course.course_name}</td>
                        <td>
                          <span className="badge bg-secondary">{course.stream}</span>
                        </td>
                        <td>
                          {course.lecturer_name ? (
                            course.lecturer_name
                          ) : (
                            <select 
                              className="form-select form-select-sm"
                              onChange={(e) => handleAssignLecturer(course.id, e.target.value)}
                            >
                              <option value="">Assign Lecturer</option>
                              {lecturers.map(lecturer => (
                                <option key={lecturer.id} value={lecturer.id}>
                                  {lecturer.name}
                                </option>
                              ))}
                            </select>
                          )}
                        </td>
                        <td>
                          <span className="badge bg-primary">{course.report_count}</span>
                        </td>
                        <td>{renderStars(course.average_rating)}</td>
                        <td>
                          <button className="btn btn-sm btn-outline-primary me-1">
                            Edit
                          </button>
                          <button className="btn btn-sm btn-outline-danger">
                            Delete
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Reports Tab */}
      {activeTab === 'reports' && (
        <div className="card">
          <div className="card-header d-flex justify-content-between align-items-center">
            <div>
              <h5 className="card-title mb-0">Reports with PRL Feedback</h5>
              <small className="text-muted">View reports that have received Principal Lecturer feedback</small>
            </div>
            <ExportButton
              exportFunction={exportAPI.reports}
              filename="program-reports"
              buttonText="Export Reports"
              variant="success"
              size="sm"
            />
          </div>
          <div className="card-body">
            {reports.length === 0 ? (
              <div className="text-center py-4">
                <h5>No Reports Available</h5>
                <p>There are no reports with PRL feedback yet.</p>
              </div>
            ) : (
              <div>
                {reports.map(report => (
                  <div key={report.id} className="card mb-3">
                    <div className="card-body">
                      <div className="d-flex justify-content-between align-items-start mb-2">
                        <div>
                          <h6 className="card-title">{report.course_code} - {report.course_name}</h6>
                          <p className="card-text mb-1">
                            <strong>Lecturer:</strong> {report.lecturer_name} | 
                            <strong> PRL:</strong> {report.principal_name} | 
                            <strong> Class:</strong> {report.class_name} | 
                            <strong> Date:</strong> {new Date(report.date_of_lecture).toLocaleDateString()}
                          </p>
                        </div>
                        <div className="text-end">
                          <span className="badge bg-success me-2">{report.actual_students_present} students</span>
                          {renderStars(report.average_rating)}
                        </div>
                      </div>
                      
                      <p><strong>Topic:</strong> {report.topic_taught}</p>
                      
                      <div className="bg-light p-3 rounded">
                        <strong>PRL Feedback:</strong>
                        <p className="mb-0 mt-1">{report.principal_feedback}</p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      )}

      {/* Lecturers Tab */}
      {activeTab === 'lecturers' && (
        <div className="card">
          <div className="card-header">
            <h5 className="card-title mb-0">Lecturer Management</h5>
          </div>
          <div className="card-body">
            <div className="table-responsive">
              <table className="table table-striped">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Faculty</th>
                    <th>Assigned Courses</th>
                    <th>Reports</th>
                    <th>Avg Rating</th>
                    <th>Performance</th>
                  </tr>
                </thead>
                <tbody>
                  {lecturers.map(lecturer => (
                    <tr key={lecturer.id}>
                      <td><strong>{lecturer.name}</strong></td>
                      <td>{lecturer.email}</td>
                      <td>
                        <span className="badge bg-info">{lecturer.faculty}</span>
                      </td>
                      <td>
                        <span className="badge bg-primary">{lecturer.assigned_courses}</span>
                      </td>
                      <td>
                        <span className="badge bg-secondary">{lecturer.total_reports}</span>
                      </td>
                      <td>{renderStars(lecturer.average_rating)}</td>
                      <td>
                        {lecturer.average_rating >= 4.0 ? (
                          <span className="badge bg-success">Excellent</span>
                        ) : lecturer.average_rating >= 3.0 ? (
                          <span className="badge bg-warning">Good</span>
                        ) : lecturer.average_rating ? (
                          <span className="badge bg-danger">Needs Improvement</span>
                        ) : (
                          <span className="badge bg-light text-dark">No Data</span>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}

      {/* Classes Tab */}
      {activeTab === 'classes' && (
        <div className="card">
          <div className="card-header">
            <h5 className="card-title mb-0">Class Management</h5>
          </div>
          <div className="card-body">
            <div className="table-responsive">
              <table className="table table-striped">
                <thead>
                  <tr>
                    <th>Class Name</th>
                    <th>Faculty</th>
                    <th>Registered Students</th>
                    <th>Venue</th>
                    <th>Reports</th>
                    <th>Avg Attendance</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {classes.map(cls => (
                    <tr key={cls.id}>
                      <td><strong>{cls.class_name}</strong></td>
                      <td>
                        <span className="badge bg-info">{cls.faculty}</span>
                      </td>
                      <td>{cls.total_registered_students}</td>
                      <td>{cls.venue}</td>
                      <td>
                        <span className="badge bg-primary">{cls.report_count}</span>
                      </td>
                      <td>
                        <span className="badge bg-success">{cls.avg_attendance?.toFixed(1) || 0}%</span>
                      </td>
                      <td>
                        <button className="btn btn-sm btn-outline-primary me-1">
                          Edit
                        </button>
                        <button className="btn btn-sm btn-outline-info">
                          View Details
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}

      {/* Monitoring Tab */}
      {activeTab === 'monitoring' && (
        <div>
          <div className="card">
            <div className="card-header">
              <h5 className="card-title mb-0">Program Monitoring</h5>
              <small className="text-muted">Overall program performance and health</small>
            </div>
            <div className="card-body">
              <div className="row mb-4">
                <div className="col-md-6">
                  <h6>Course Assignment Rate</h6>
                  <div className="progress mb-3">
                    <div 
                      className="progress-bar bg-success" 
                      style={{ width: `${(statistics.courses.assigned_courses / statistics.courses.total_courses * 100).toFixed(1)}%` }}
                    >
                      {((statistics.courses.assigned_courses / statistics.courses.total_courses) * 100).toFixed(1)}%
                    </div>
                  </div>
                  
                  <h6>Lecturer Activity Rate</h6>
                  <div className="progress mb-3">
                    <div 
                      className="progress-bar bg-info" 
                      style={{ width: `${(statistics.reports.active_lecturers / statistics.lecturers.total_lecturers * 100).toFixed(1)}%` }}
                    >
                      {((statistics.reports.active_lecturers / statistics.lecturers.total_lecturers) * 100).toFixed(1)}%
                    </div>
                  </div>
                </div>
                
                <div className="col-md-6">
                  <h6>Student Satisfaction</h6>
                  <div className="progress mb-3">
                    <div 
                      className="progress-bar bg-warning" 
                      style={{ width: `${(statistics.ratings.avg_rating / 5 * 100).toFixed(1)}%` }}
                    >
                      {statistics.ratings.avg_rating?.toFixed(1)}/5.0
                    </div>
                  </div>
                  
                  <h6>Report Submission Rate</h6>
                  <div className="progress mb-3">
                    <div 
                      className="progress-bar bg-primary" 
                      style={{ width: '78%' }}
                    >
                      78% Expected Reports
                    </div>
                  </div>
                </div>
              </div>
              
              <div className="mt-4">
                <h6>Program Health Indicators</h6>
                <div className="row">
                  <div className="col-md-4">
                    <div className="card bg-light">
                      <div className="card-body text-center">
                        <h5></h5>
                        <h6>Course Coverage</h6>
                        <span className="badge bg-success">Excellent</span>
                      </div>
                    </div>
                  </div>
                  <div className="col-md-4">
                    <div className="card bg-light">
                      <div className="card-body text-center">
                        <h5></h5>
                        <h6>Lecturer Engagement</h6>
                        <span className="badge bg-success">Good</span>
                      </div>
                    </div>
                  </div>
                  <div className="col-md-4">
                    <div className="card bg-light">
                      <div className="card-body text-center">
                        <h5></h5>
                        <h6>Student Satisfaction</h6>
                        <span className="badge bg-warning">Satisfactory</span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProgramLeaderDashboard;
