import React, { useState, useEffect } from 'react';
import { reportsAPI, ratingsAPI } from '../../services/api';

const StudentDashboard = () => {
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('monitoring');

  useEffect(() => {
    loadStudentReports();
  }, []);

  const loadStudentReports = async () => {
    try {
      // For now, using mock data - replace with actual API call
      // const response = await reportsAPI.getStudentReports(user.faculty);
      // setReports(response.data);
      
      // Mock data for testing
      setTimeout(() => {
        setReports([
          {
            id: 1,
            week_of_reporting: 'Week 6',
            date_of_lecture: '2024-10-01',
            class_name: 'BIT-1A',
            course_name: 'Web Application Development',
            course_code: 'DIWA2110',
            lecturer_name: 'Dr. John Smith',
            actual_students_present: 38,
            topic_taught: 'Introduction to React and Component Architecture',
            learning_outcomes: 'Students should understand React components and props',
            average_rating: 4.2,
            rating_count: 15
          },
          {
            id: 2,
            week_of_reporting: 'Week 5',
            date_of_lecture: '2024-09-24',
            class_name: 'BIT-1A',
            course_name: 'Network Management',
            course_code: 'DINM2110',
            lecturer_name: 'Prof. Sarah Johnson',
            actual_students_present: 42,
            topic_taught: 'Network Protocols and TCP/IP Model',
            learning_outcomes: 'Understanding of network layers and protocols',
            average_rating: 4.5,
            rating_count: 12
          }
        ]);
        setLoading(false);
      }, 1000);
    } catch (error) {
      console.error('Error loading reports:', error);
      setLoading(false);
    }
  };

  const renderStars = (rating) => {
    return (
      <div className="star-rating">
        {[1, 2, 3, 4, 5].map(star => (
          <span 
            key={star} 
            className={star <= rating ? 'text-warning' : 'text-muted'}
          >
            ‚òÖ
          </span>
        ))}
        <small className="text-muted ms-2">({rating})</small>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p>Loading your class reports...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h3>Student Dashboard</h3>
        <span className="badge bg-primary">Faculty: {user.faculty}</span>
      </div>

      {/* Navigation Tabs */}
      <ul className="nav nav-tabs mb-4">
        <li className="nav-item">
          <button 
            className={`nav-link ${activeTab === 'monitoring' ? 'active' : ''}`}
            onClick={() => setActiveTab('monitoring')}
          >
            üìä Class Monitoring
          </button>
        </li>
        <li className="nav-item">
          <button 
            className={`nav-link ${activeTab === 'my-ratings' ? 'active' : ''}`}
            onClick={() => setActiveTab('my-ratings')}
          >
            ‚≠ê My Ratings
          </button>
        </li>
      </ul>

      {/* Monitoring Tab */}
      {activeTab === 'monitoring' && (
        <div>
          <div className="card">
            <div className="card-header">
              <h5 className="card-title mb-0">Class Reports & Attendance</h5>
              <small className="text-muted">View teaching reports from your classes</small>
            </div>
            <div className="card-body">
              {reports.length === 0 ? (
                <div className="text-center py-4">
                  <h5>No Reports Available</h5>
                  <p>There are no teaching reports for your classes yet.</p>
                </div>
              ) : (
                <div className="table-responsive">
                  <table className="table table-striped">
                    <thead>
                      <tr>
                        <th>Date</th>
                        <th>Course</th>
                        <th>Lecturer</th>
                        <th>Topic</th>
                        <th>Attendance</th>
                        <th>Rating</th>
                        <th>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {reports.map(report => (
                        <tr key={report.id}>
                          <td>
                            <small>
                              {new Date(report.date_of_lecture).toLocaleDateString()}
                              <br />
                              <span className="text-muted">{report.week_of_reporting}</span>
                            </small>
                          </td>
                          <td>
                            <strong>{report.course_code}</strong>
                            <br />
                            <small>{report.course_name}</small>
                          </td>
                          <td>{report.lecturer_name}</td>
                          <td>
                            <small>{report.topic_taught.substring(0, 40)}...</small>
                          </td>
                          <td>
                            <span className={`badge ${report.actual_students_present > 35 ? 'bg-success' : 'bg-warning'}`}>
                              {report.actual_students_present} present
                            </span>
                          </td>
                          <td>
                            {report.average_rating > 0 ? (
                              renderStars(report.average_rating)
                            ) : (
                              <span className="text-muted">No ratings</span>
                            )}
                            <br />
                            <small className="text-muted">{report.rating_count} ratings</small>
                          </td>
                          <td>
                            <button 
                              className="btn btn-sm btn-outline-primary me-1"
                              onClick={() => window.location.href = `/student/report/${report.id}`}
                            >
                              View
                            </button>
                            <button 
                              className="btn btn-sm btn-success"
                              onClick={() => window.location.href = `/student/rate/${report.id}`}
                            >
                              Rate
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          </div>

          {/* Quick Stats */}
          <div className="row mt-4">
            <div className="col-md-3">
              <div className="card text-white bg-info">
                <div className="card-body">
                  <h5 className="card-title">{reports.length}</h5>
                  <p className="card-text">Total Reports</p>
                </div>
              </div>
            </div>
            <div className="col-md-3">
              <div className="card text-white bg-success">
                <div className="card-body">
                  <h5 className="card-title">
                    {reports.filter(r => r.average_rating > 0).length}
                  </h5>
                  <p className="card-text">Rated Lectures</p>
                </div>
              </div>
            </div>
            <div className="col-md-3">
              <div className="card text-white bg-warning">
                <div className="card-body">
                  <h5 className="card-title">
                    {new Set(reports.map(r => r.course_name)).size}
                  </h5>
                  <p className="card-text">Courses</p>
                </div>
              </div>
            </div>
            <div className="col-md-3">
              <div className="card text-white bg-primary">
                <div className="card-body">
                  <h5 className="card-title">
                    {new Set(reports.map(r => r.lecturer_name)).size}
                  </h5>
                  <p className="card-text">Lecturers</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* My Ratings Tab */}
      {activeTab === 'my-ratings' && (
        <div>
          <div className="card">
            <div className="card-header">
              <h5 className="card-title mb-0">My Ratings</h5>
              <small className="text-muted">Lectures you have rated</small>
            </div>
            <div className="card-body">
              <div className="text-center py-4">
                <h5>My Ratings Feature</h5>
                <p>This will show the ratings you've submitted for various lectures.</p>
                <button className="btn btn-primary">View My Rating History</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default StudentDashboard;
