import React, { useState } from 'react';

const ModuleSearch = ({ onSearch, placeholder = "Search...", size = "md" }) => {
  const [query, setQuery] = useState('');

  const handleSearch = (e) => {
    const value = e.target.value;
    setQuery(value);
    
    // Debounce the search
    clearTimeout(window.moduleSearchTimeout);
    window.moduleSearchTimeout = setTimeout(() => {
      onSearch(value);
    }, 300);
  };

  const clearSearch = () => {
    setQuery('');
    onSearch('');
  };

  const inputClass = size === "sm" ? "form-control form-control-sm" : "form-control";

  return (
    <div className="module-search">
      <div className="input-group">
        <input
          type="text"
          className={inputClass}
          placeholder={placeholder}
          value={query}
          onChange={handleSearch}
        />
        {query ? (
          <button 
            className={`btn btn-outline-secondary ${size === 'sm' ? 'btn-sm' : ''}`}
            type="button"
            onClick={clearSearch}
          >
            ‚úï
          </button>
        ) : (
          <button className={`btn btn-outline-secondary ${size === 'sm' ? 'btn-sm' : ''}`} type="button">
            üîç
          </button>
        )}
      </div>
    </div>
  );
};

export default ModuleSearch;
